import React from "react";
import { useDispatch, useSelector } from "react-redux";

export default function Banner({ isMarket }) {
  const dispatch = useDispatch();
  const market = useSelector((state) => state.m_banners);
  const beauty = useSelector((state) => state.b_banners);
  const banners = isMarket ? market : beauty;

  return (
    <>
      {/* ë°°ë„ˆ  */}
      <div className="banner">
        <img
          src="data:image/svg+xml;base64,"
          alt="none"
          className="arrowL"
        />
        <img src={banners[0]} alt="none" style={{ marginTop: "10px" }} />
        <img
          src="data:image/svg+xml;base64,"
          alt="none"
          className="arrowR"
        />
      </div>
    </>
  );
}
